import { useState } from "react";
import { auth } from "../firebase"; 
import { getDatabase, ref, get } from "../servcices/firebase";
import { createUserWithEmailAndPassword, sendEmailVerification } from "firebase/auth";

export default function Login() {
  const [isSignup, setIsSignup] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [generatedOtp, setGeneratedOtp] = useState("");
  const [enteredOtp, setEnteredOtp] = useState("");

  // ðŸ”¹ Get email from DB and send OTP
  const sendOtpToAdminEmail = async () => {
    try {
      const db = getDatabase();
      const otpEmailRef = ref(db, "admin_information/admin_email_otp");
      const snapshot = await get(otpEmailRef);

      if (snapshot.exists()) {
        const adminEmail = snapshot.val();
        const otp = Math.floor(100000 + Math.random() * 900000).toString();
        setGeneratedOtp(otp);

        // ðŸ”¹ Use your backend or email service to send OTP to adminEmail
        await fetch("/api/send-otp", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ to: adminEmail, otp }),
        });

        setOtpSent(true);
        alert("OTP sent to admin email.");
      } else {
        alert("Admin OTP email not found in database.");
      }
    } catch (err) {
      console.error(err);
      alert("Failed to send OTP.");
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    const email = e.target.email.value.trim();
    const password = e.target.password.value.trim();

    if (!otpSent) {
      await sendOtpToAdminEmail();
      return;
    }

    if (enteredOtp !== generatedOtp) {
      alert("Invalid OTP. Please try again.");
      return;
    }

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      await sendEmailVerification(userCredential.user);

      alert("Signup successful! Check your email to verify before login.");
      setIsSignup(false);
      setOtpSent(false);
      setEnteredOtp("");
    } catch (error) {
      console.error(error);
      alert("Signup failed: " + error.message);
    }
  };

  return (
    <div className="login-container">
      <div className="card">
        <div className="text-center mb-3">
          <img src="/images/laundrylogo.jpg" alt="Lola Fe's Laundry PH Logo" className="img-fluid" />
        </div>

        <h3 className="text-center">
          {isSignup ? "Create Admin Account" : "Welcome to Lola Fe's Laundry PH"}
        </h3>
        <p className="text-center">
          {isSignup ? "Sign up to register admin" : "Sign in to continue"}
        </p>

        {!isSignup ? (
          <>
            {/* Google Login */}
            <button className="btn-google" onClick={handleGoogleLogin}>
              <img src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg" alt="Google Logo" />
              Continue with Google
            </button>

            <div className="d-flex align-items-center">
              <hr className="divider flex-grow-1" />
              <span className="divider-text">OR</span>
              <hr className="divider flex-grow-1" />
            </div>

            <form onSubmit={handleLogin}>
              <div className="mb-3 text-start">
                <label htmlFor="email" className="form-label">Email</label>
                <input type="email" id="email" className="form-control" placeholder="you@example.com" required />
              </div>

              <div className="mb-3 text-start">
                <label htmlFor="password" className="form-label">Password</label>
                <input type="password" id="password" className="form-control" placeholder="********" required />
              </div>

              <button type="submit" className="btn-primary">Sign in</button>
            </form>

            <div className="footer-links mt-4">
              <a href="#">Forgot password?</a>
              <button className="btn-link" onClick={() => setIsSignup(true)}>Sign up</button>
            </div>
          </>
        ) : (
          <form onSubmit={handleSignup}>
            <div className="mb-3 text-start">
              <label htmlFor="email" className="form-label">Admin Email</label>
              <input type="email" id="email" className="form-control" placeholder="admin@example.com" required />
            </div>

            <div className="mb-3 text-start">
              <label htmlFor="password" className="form-label">Password</label>
              <input type="password" id="password" className="form-control" placeholder="********" required />
            </div>

            {otpSent && (
              <div className="mb-3 text-start">
                <label htmlFor="otp" className="form-label">Enter OTP</label>
                <input
                  type="text"
                  id="otp"
                  className="form-control"
                  placeholder="6-digit OTP"
                  value={enteredOtp}
                  onChange={(e) => setEnteredOtp(e.target.value)}
                  required
                />
              </div>
            )}

            <button type="submit" className="btn-primary">
              {otpSent ? "Complete Signup" : "Send OTP"}
            </button>

            <div className="footer-links mt-4">
              <button className="btn-link" onClick={() => setIsSignup(false)}>Back to Login</button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}
