// src/components/ui/UI.js
import React, { useEffect, useState } from "react";
import "../style/modal-style/LoginPopup.css";

export function LoginPopup({ message, type = "info", onClose }) {
  const [progress, setProgress] = useState(100); // start at 100%

  useEffect(() => {
    if (message) {
      setProgress(100);

      // Shrink bar every 40ms → ~2 seconds
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev <= 0) {
            clearInterval(interval);
            onClose();
            return 0;
          }
          return prev - 2.5;
        });
      }, 40);

      return () => clearInterval(interval);
    }
  }, [message, onClose]);

  if (!message) return null;

  const borderClass =
    type === "success" ? "border-success text-success" : "border-danger text-danger";

  const icon =
    type === "success" ? (
      <svg
        xmlns="http://www.w3.org/2000/svg"
        width="18"
        height="18"
        fill="currentColor"
        className="bi bi-check-circle me-2"
        viewBox="0 0 16 16"
      >
        <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
        <path d="m10.97 4.97-.02.022-3.473 4.425-2.093-2.094a.75.75 0 0 0-1.06 1.06L6.97 11.03a.75.75 0 0 0 1.079-.02l3.992-4.99a.75.75 0 0 0-1.071-1.05" />
      </svg>
    ) : (
      <svg
        xmlns="http://www.w3.org/2000/svg"
        width="18"
        height="18"
        fill="currentColor"
        className="bi bi-exclamation-circle me-2"
        viewBox="0 0 16 16"
      >
        <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 0 0 8 0a8 8 0 0 0 0 16" />
        <path d="M7.002 11a1 1 0 1 1 2 0 1 1 0 0 1-2 0M7.1 4.995a.905.905 0 1 1 1.8 0l-.35 3.507a.552.552 0 0 1-1.1 0z" />
      </svg>
    );

  return (
    <div
      className="position-fixed top-0 start-50 translate-middle-x mt-3"
      style={{ zIndex: 1055, minWidth: "280px" }}
    >
      <div
        className={`toast show bg-white shadow-sm border border-5 rounded ${borderClass}`}
        role="alert"
      >
        {/* Close Button */}
        <button
          type="button"
          className="btn-close position-absolute top-0 end-0 m-2"
          aria-label="Close"
          onClick={onClose}
        ></button>

        {/* Message with Icon */}
        <div className="toast-body d-flex align-items-center justify-content-center">
          {icon}
          <span>{message}</span>
        </div>

        {/* Progress Bar */}
        <div
          className={`progress-bar ${type}`}
          style={{
            height: "4px",
            backgroundColor: type === "success" ? "green" : "red",
            width: `${progress}%`,
            transition: "width 40ms linear",
          }}
        ></div>
      </div>
    </div>
  );
}
