import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../style/neworder.css"; // ✅ Connect to your custom CSS

function NewOrder() {
  const [customer, setCustomer] = useState({
    name: "",
    phone: "",
    address: "",
  });

  const [services, setServices] = useState([]);
  const [total, setTotal] = useState(0);

  const serviceOptions = [
    { name: "Regular Wash & Dry", price: 25 },
    { name: "Wash, Dry & Fold", price: 35 },
    { name: "Premium Wash, Dry & Press", price: 45 },
  ];

  const addService = (service, weight) => {
    if (!weight || weight <= 0) return;
    const subtotal = service.price * weight;

    const newService = { ...service, weight, subtotal };
    setServices([...services, newService]);
    setTotal(total + subtotal);
  };

  const createOrder = () => {
    if (!customer.name || !customer.phone || services.length === 0) {
      alert("Please fill customer details and add services.");
      return;
    }
    alert("✅ Order Created Successfully!");
  };

  return (
    <div className="dashboard-container">
      {/* Main Content */}
      <main className="main-content">
        <header>
          <h2>New Order</h2>
          <p>Create a new laundry order</p>
        </header>

        <div className="row g-4">
          {/* Left Column */}
          <section className="col-md-7">
            <div className="new-order">
              <h3>👤 Customer Information</h3>
              <div className="form-group">
                <label>Customer Name *</label>
                <input
                  type="text"
                  value={customer.name}
                  onChange={(e) =>
                    setCustomer({ ...customer, name: e.target.value })
                  }
                />
              </div>
              <div className="form-group">
                <label>Phone Number *</label>
                <input
                  type="text"
                  value={customer.phone}
                  onChange={(e) =>
                    setCustomer({ ...customer, phone: e.target.value })
                  }
                />
              </div>
              <div className="form-group">
                <label>Address</label>
                <input
                  type="text"
                  value={customer.address}
                  onChange={(e) =>
                    setCustomer({ ...customer, address: e.target.value })
                  }
                />
              </div>
            </div>

            <div className="new-order">
              <h3>📦 Services</h3>
              {serviceOptions.map((service, index) => (
                <div
                  key={index}
                  className="d-flex justify-content-between align-items-center border p-2 rounded mb-2"
                >
                  <div>
                    <h6 className="mb-0">{service.name}</h6>
                    <small>₱{service.price}/kg</small>
                  </div>
                  <div className="d-flex">
                    <input
                      type="number"
                      className="form-control form-control-sm me-2"
                      placeholder="Kg"
                      id={`weight-${index}`}
                    />
                    <button
                      className="btn btn-success btn-sm"
                      onClick={() => {
                        const weight = document.getElementById(
                          `weight-${index}`
                        ).value;
                        addService(service, parseFloat(weight));
                      }}
                    >
                      +
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Right Column */}
          <aside className="col-md-5">
            <div className="new-order">
              <h3>📋 Order Summary</h3>
              <div className="mb-3">
                <strong>Customer</strong>
                <br />
                {customer.name || "No name"} <br />
                {customer.phone || "No phone"} <br />
                {customer.address || ""}
              </div>

              <ul className="list-unstyled mb-3">
                <strong>Services ({services.length})</strong>
                {services.length === 0 ? (
                  <p>No services selected</p>
                ) : (
                  services.map((s, i) => (
                    <li key={i}>
                      {s.weight}kg {s.name} — ₱{s.subtotal}
                    </li>
                  ))
                )}
              </ul>

              <p>
                <strong>Total: ₱{total}</strong>
              </p>

              <div className="form-group">
                <label>Special Instructions</label>
                <textarea rows="2" placeholder="Add notes..." />
              </div>

              <div className="form-group">
                <label>Payment Method</label>
                <select>
                  <option>Cash</option>
                  <option>GCash</option>
                  <option>Card</option>
                </select>
              </div>

              <div className="form-actions">
                <button className="btn-submit" onClick={createOrder}>
                  Create Order
                </button>
              </div>
            </div>
          </aside>
        </div>
      </main>
    </div>
  );
}

export default NewOrder;
