import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../style/neworder.css"; // âœ… Connect to your custom CSS

function NewOrder() {
  const [customer, setCustomer] = useState({
    name: "",
    phone: "",
    address: "",
  });

  const [services, setServices] = useState([]);
  const [total, setTotal] = useState(0);

  const serviceOptions = [
    { name: "Regular Wash & Dry", type: "Wash & Dry", price: 25 },
    { name: "Wash, Dry & Fold", type: "Wash, Dry & Fold", price: 35 },
    { name: "Premium Wash, Dry & Press", type: "Wash, Dry & Press", price: 45 },
  ];

  const addService = (service, weight) => {
    if (!weight || weight <= 0) return;
    const subtotal = service.price * weight;

    const newService = { ...service, weight, subtotal };
    setServices([...services, newService]);
    setTotal(total + subtotal);
  };

  const createOrder = () => {
    if (!customer.name || !customer.phone || services.length === 0) {
      alert("Please fill customer details and add services.");
      return;
    }
    alert("âœ… Order Created Successfully!");
  };

  return (
    <div className="dashboard-container">
      {/* Main Content */}
      <main className="main-content">
        <header>
          <h2>New Order</h2>
          <p>Create a new laundry order</p>
        </header>

        <div className="row g-4">
          {/* Left Column */}
          <section className="col-md-7">
            <div className="new-order">
              <h3>ðŸ‘¤ Customer Information</h3>
              <div className="form-group">
                <label>Customer Name *</label>
                <input
                  type="text"
                  value={customer.name}
                  onChange={(e) =>
                    setCustomer({ ...customer, name: e.target.value })
                  }
                />
              </div>
              <div className="form-group">
                <label>Phone Number *</label>
                <input
                  type="text"
                  value={customer.phone}
                  onChange={(e) =>
                    setCustomer({ ...customer, phone: e.target.value })
                  }
                />
              </div>
              <div className="form-group">
                <label>Address</label>
                <input
                  type="text"
                  value={customer.address}
                  onChange={(e) =>
                    setCustomer({ ...customer, address: e.target.value })
                  }
                />
              </div>
            </div>

            {/* Services Section */}
            <div className="new-order">
              <h3>ðŸ“¦ Services</h3>
              <p className="text-muted mb-3">Available Services</p>
              {serviceOptions.map((service, index) => (
                <div
                  key={index}
                  className="service-card d-flex justify-content-between align-items-center border rounded p-3 mb-3"
                >
                  {/* Left side: Service name + tags + price */}
                  <div>
                    <h6 className="mb-1 fw-bold">{service.name}</h6>
                    <div className="d-flex flex-wrap align-items-center gap-2">
                      <span className="badge bg-primary-subtle text-primary">
                        {service.type}
                      </span>
                      <span className="badge bg-light text-dark border">
                        â‚±{service.price}/kg
                      </span>
                    </div>
                  </div>

                  {/* Right side: Weight input + add button */}
                  <div className="d-flex align-items-center">
                    <input
                      type="number"
                      className="form-control form-control-sm me-2"
                      placeholder="Weight (kg)"
                      id={`weight-${index}`}
                      style={{ width: "120px" }}
                    />
                    <button
                      className="btn btn-primary btn-sm fw-bold"
                      style={{
                        backgroundColor: "#2563eb",
                        border: "none",
                        color: "#fff",
                        fontSize: "16px",
                        width: "36px",
                        height: "36px",
                        borderRadius: "8px",
                      }}
                      onClick={() => {
                        const weight = document.getElementById(
                          `weight-${index}`
                        ).value;
                        addService(service, parseFloat(weight));
                      }}
                    >
                      +
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Right Column */}
          <aside className="col-md-5">
            <div className="new-order">
              <h3>ðŸ“‹ Order Summary</h3>
              <div className="mb-3">
                <strong>Customer</strong>
                <br />
                {customer.name || "No name"} <br />
                {customer.phone || "No phone"} <br />
                {customer.address || ""}
              </div>

              <ul className="list-unstyled mb-3">
                <strong>Services ({services.length})</strong>
                {services.length === 0 ? (
                  <p>No services selected</p>
                ) : (
                  services.map((s, i) => (
                    <li key={i}>
                      {s.weight}kg {s.name} â€” â‚±{s.subtotal}
                    </li>
                  ))
                )}
              </ul>

              <p>
                <strong>Total: â‚±{total}</strong>
              </p>

              <div className="form-group">
                <label>Special Instructions</label>
                <textarea rows="2" placeholder="Add notes..." />
              </div>

              <div className="form-group">
                <label>Payment Method</label>
                <select>
                  <option>Cash</option>
                  <option>GCash</option>
                  <option>Card</option>
                </select>
              </div>

              <div className="form-actions">
                <button className="btn-submit" onClick={createOrder}>
                  Create Order
                </button>
              </div>
            </div>
          </aside>
        </div>
      </main>
    </div>
  );
}

export default NewOrder;
