// src/components/ui/UI.js
import React, { useEffect } from "react";

// Generic Modal / Pop-up
export function LoginPopup({ message, type = "info", onClose }) {
  useEffect(() => {
    if (message) {
      const timer = setTimeout(() => {
        onClose();
      }, 2000); // auto-close after 2 seconds
      return () => clearTimeout(timer);
    }
  }, [message, onClose]);

  if (!message) return null;

  // Set styles depending on type
  const borderColor = type === "success" ? "green" : type === "error" ? "red" : "blue";
  const textColor = borderColor;

  return (
    <div
      className="position-fixed top-0 start-50 translate-middle-x mt-3"
      style={{ zIndex: 1055, minWidth: "250px" }}
    >
      <div
        className="toast show"
        role="alert"
        style={{
          border: `2px solid ${borderColor}`,
          backgroundColor: "white",
          color: textColor,
          fontWeight: "500",
        }}
      >
        <div className="toast-body text-center">{message}</div>
      </div>
    </div>
  );
}
