import { getDatabase, ref, get } from "firebase/database";
import { createUserWithEmailAndPassword, sendEmailVerification, fetchSignInMethodsForEmail } from "firebase/auth";
import { auth } from "../services/firebase";
import emailjs from "@emailjs/browser";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { LoginPopup } from "../modal/LoginPopup";

export default function SignUp() {
  const navigate = useNavigate();

  const [otpSent, setOtpSent] = useState(false);
  const [generatedOtp, setGeneratedOtp] = useState("");
  const [enteredOtp, setEnteredOtp] = useState("");
  const [popup, setPopup] = useState({ message: "", type: "info" });

  const closePopup = () => setPopup({ message: "", type: "info" });

  // ðŸ”¹ Fetch admin email from Realtime DB
  const fetchAdminEmail = async () => {
    const db = getDatabase();
    const otpEmailRef = ref(db, "admin_information/admin_email_otp");
    const snapshot = await get(otpEmailRef);

    if (snapshot.exists()) {
      return snapshot.val();
    } else {
      throw new Error("Admin OTP email not found in database.");
    }
  };

  // ðŸ”¹ Send OTP using EmailJS
  const sendOtpToAdminEmail = async () => {
    try {
      const adminEmail = await fetchAdminEmail();
      const otp = Math.floor(100000 + Math.random() * 900000).toString();
      setGeneratedOtp(otp);

      await emailjs.send(
        "service_j61dqwv",
        "template_jijquaz",
        { email: adminEmail, otp },
        "evCxW5Fmv_kR4q8gh"
      );

      setOtpSent(true);
      setPopup({ message: "OTP sent to admin email.", type: "success" });
    } catch (err) {
      console.error(err);
      setPopup({ message: "Failed to send OTP: " + err.message, type: "error" });
    }
  };

  const handleSignup = async (e) => {
  e.preventDefault();
  const email = e.target.email.value.trim();
  const password = e.target.password.value.trim();

  if (!otpSent) {
    await sendOtpToAdminEmail();
    return;
  }

  if (enteredOtp !== generatedOtp) {
    setPopup({ message: "Invalid OTP. Please try again.", type: "error" });
    return;
  }

  try {
    // ðŸ”¹ Check if email already exists
    const methods = await fetchSignInMethodsForEmail(auth, email);
    if (methods.length > 0) {
      setPopup({ message: "Email is already registered. Please log in.", type: "error" });
      return;
    }

    const userCredential = await createUserWithEmailAndPassword(auth, email, password);
    await sendEmailVerification(userCredential.user);
    setPopup({ message: "Signup successful! Check your email to verify.", type: "success" });
    setOtpSent(false);
    setEnteredOtp("");
  } catch (error) {
    console.error(error);
    setPopup({ message: "Signup failed: " + error.message, type: "error" });
  }
};

  return (
    <div className="login-container">
      <LoginPopup message={popup.message} type={popup.type} onClose={closePopup} />

      <div className="card">
        <h3 className="text-center">Create Admin Account</h3>
        <p className="text-center">Sign up with OTP verification</p>

        <form onSubmit={handleSignup}>
          <div className="mb-3 text-start">
            <label htmlFor="email" className="form-label">Admin Email</label>
            <input type="email" id="email" className="form-control" placeholder="admin@example.com" required />
          </div>

          <div className="mb-3 text-start">
            <label htmlFor="password" className="form-label">Password</label>
            <input type="password" id="password" className="form-control" placeholder="********" required />
          </div>

          {otpSent && (
            <div className="mb-3 text-start">
              <label htmlFor="otp" className="form-label">Enter OTP</label>
              <input
                type="text"
                id="otp"
                className="form-control"
                placeholder="6-digit OTP"
                value={enteredOtp}
                onChange={(e) => setEnteredOtp(e.target.value)}
                required
              />
            </div>
          )}

          <button type="submit" className="btn-primary">
            {otpSent ? "Complete Signup" : "Send OTP"}
          </button>
        </form>

        {/* Login Button */}
        <div className="text-center mt-3">
          <p>
            Already have an account?{" "}
            <button
              onClick={() => navigate("/login")}
              className="btn-link"
              style={{ border: "none", background: "none", color: "#0d6efd", cursor: "pointer" }}
            >
              Login
            </button>
          </p>
        </div>
      </div>
    </div>
  );
}
