// src/components/ui/UI.js
import React from "react";
import "../style/ui.css"; // optional, for styling

// Generic Modal / Pop-up
export function LoginPopup({ message, type = "info", onClose }) {
  if (!message) return null;

  const borderClass =
    type === "success"
      ? "border-start border-5 border-success"
      : type === "error"
      ? "border-start border-5 border-danger"
      : "";

  return (
    <div className="modal show d-block" tabIndex="-1" role="dialog">
      <div className="modal-dialog modal-dialog-centered" role="document">
        <div className={`modal-content ${borderClass}`}>
          <div className="modal-body text-center">
            <p className="mb-3">{message}</p>
            <button className="btn btn-primary" onClick={onClose}>
              OK
            </button>
          </div>
        </div>
      </div>
      <div className="modal-backdrop show"></div>
    </div>
  );
}
