// src/components/ui/UI.js
import React, { useEffect } from "react";
import '../style/modal-style/LoginPopup.css';

// Generic Modal / Pop-up
export function LoginPopup({ message, type = "info", onClose }) {
  useEffect(() => {
    if (message) {
      const timer = setTimeout(() => {
        onClose();
      }, 2000); // auto-close after 2 seconds
      return () => clearTimeout(timer);
    }
  }, [message, onClose]);

  if (!message) return null;

  const bgClass =
    type === "success"
      ? "bg-white text-success border-success"
      : type === "error"
      ? "bg-white text-danger border-danger"
      : "bg-white text-secondary border-secondary";

  const icon =
    type === "success" ? (
      <i className="bi bi-check-circle me-2"></i>
    ) : type === "error" ? (
      <i className="bi bi-exclamation-diamond me-2"></i>
    ) : null;

  return (
    <div
      className="position-fixed top-0 start-50 translate-middle-x mt-3"
      style={{ zIndex: 1055, minWidth: "280px" }}
    >
      <div className={`toast show ${bgClass} border-2 shadow-sm`} role="alert">
        <div className="toast-body d-flex align-items-center justify-content-center fw-medium">
          {icon}
          <span>{message}</span>
        </div>
      </div>
    </div>
  );
}
