import React from "react";
import { useNavigate } from "react-router-dom";
import "../style/login.css";
import { auth } from "../firebase/firebase"; // your firebase.js
import { signInWithEmailAndPassword, GoogleAuthProvider, signInWithPopup } from "firebase/auth";

function Login() {
  const navigate = useNavigate();

  // Admin credentials (replace with your admin email or UID)
  const ADMIN_EMAIL = "admin@laundry.com";

  // Email/password login
  const handleLogin = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      if (user.email === ADMIN_EMAIL) {
        navigate("/dashboard");
      } else {
        alert("Access denied. You are not authorized as admin.");
        auth.signOut();
      }
    } catch (error) {
      console.error(error);
      alert("Login failed: " + error.message);
    }
  };

  // Google login
  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      if (user.email === ADMIN_EMAIL) {
        navigate("/dashboard");
      } else {
        alert("Access denied. You are not authorized as admin.");
        auth.signOut();
      }
    } catch (error) {
      console.error(error);
      alert("Google login failed: " + error.message);
    }
  };

  return (
    <div className="login-container">
      <div className="card">
        <div className="text-center mb-3">
          <img
            src="/images/laundrylogo.jpg"
            alt="Lola Fe's Laundry PH Logo"
            className="img-fluid"
          />
        </div>

        <h3 className="text-center">Welcome to Lola Fe's Laundry PH</h3>
        <p className="text-center">Sign in to continue</p>

        {/* Google Login */}
        <button className="btn-google" onClick={handleGoogleLogin}>
          <img
            src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg"
            alt="Google Logo"
          />
          Continue with Google
        </button>

        <div className="d-flex align-items-center">
          <hr className="divider flex-grow-1" />
          <span className="divider-text">OR</span>
          <hr className="divider flex-grow-1" />
        </div>

        <form onSubmit={handleLogin}>
          <div className="mb-3 text-start">
            <label htmlFor="email" className="form-label">Email</label>
            <input type="email" id="email" className="form-control" placeholder="you@example.com" required />
          </div>

          <div className="mb-3 text-start">
            <label htmlFor="password" className="form-label">Password</label>
            <input type="password" id="password" className="form-control" placeholder="********" required />
          </div>

          <button type="submit" className="btn-primary">
            Sign in
          </button>
        </form>

        <div className="footer-links mt-4">
          <a href="#">Forgot password?</a>
          <a href="#">Sign up</a>
        </div>
      </div>
    </div>
  );
}

export default Login;
