// src/components/ui/UI.js
import React, { useEffect } from "react";
import '../style/modal-style/LoginPopup.css';

// Generic Modal / Pop-up
export function LoginPopup({ message, type = "info", onClose }) {
  useEffect(() => {
    if (message) {
      const timer = setTimeout(() => {
        onClose();
      }, 2000); // auto-close after 2 seconds
      return () => clearTimeout(timer);
    }
  }, [message, onClose]);

  if (!message) return null;

  const bgClass =
    type === "success"
      ? "bg-white text-success border-success"
      : type === "error"
      ? "bg-white text-danger  border-danger"
      : "bg-info text-danger border-danger";

  const icon =
    type === "success" ? (
      <i class="bi bi-check-circle"></i>
    ) : type === "error" ? (
      <svg
        xmlns="http://www.w3.org/2000/svg"
        width="18"
        height="18"
        fill="currentColor"
        className="bi bi-exclamation-diamond me-2"
        viewBox="0 0 16 16"
      >
        <path d="M6.95.435c.58-.58 1.52-.58 2.1 0l6.515 6.516c.58.58.58 1.519 0 2.098L9.05 15.565c-.58.58-1.519.58-2.098 0L.435 9.05a1.48 1.48 0 0 1 0-2.098zm1.4.7a.495.495 0 0 0-.7 0L1.134 7.65a.495.495 0 0 0 0 .7l6.516 6.516a.495.495 0 0 0 .7 0l6.516-6.516a.495.495 0 0 0 0-.7L8.35 1.134z"/>
        <path d="M7.002 11a1 1 0 1 1 2 0 1 1 0 0 1-2 0M7.1 4.995a.905.905 0 1 1 1.8 0l-.35 3.507a.552.552 0 0 1-1.1 0z"/>
      </svg>
    ) : null;

  return (
    <div
      className={`position-fixed top-0 start-50 translate-middle-x mt-3 `}
      style={{ zIndex: 1055, minWidth: "280px" }}
    >
      <div className={`toast show ${bgClass} ${type} shadow-sm`} role="alert">
        <div className="toast-body d-flex align-items-center justify-content-center">
          {icon}
          <span>{message}</span>
        </div>
      </div>
    </div>
  );
}
