// src/components/ui/UI.js
import React from "react";
import "../style/ui.css"; // optional, for styling

// Generic Modal / Pop-up
export function LoginPopup({ message, type = "info", onClose }) {
  if (!message) return null;

  return (
    <div className="popup-overlay">
      <div className={`popup-content popup-${type}`}>
        <p>{message}</p>
        <button className="popup-close" onClick={onClose}>OK</button>
      </div>
    </div>
  );
}
