import { getDatabase, ref, get } from "firebase/database";
import { 
  createUserWithEmailAndPassword, 
  sendEmailVerification, 
  fetchSignInMethodsForEmail 
} from "firebase/auth";
import { auth } from "../services/firebase";
import emailjs from "@emailjs/browser";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { LoginPopup } from "../modal/LoginPopup";

export default function SignUp() {
  const navigate = useNavigate();

  const [otpSent, setOtpSent] = useState(false);
  const [generatedOtp, setGeneratedOtp] = useState("");
  const [enteredOtp, setEnteredOtp] = useState("");
  const [popup, setPopup] = useState({ message: "", type: "info" });

  const [password, setPassword] = useState("");
  const [criteria, setCriteria] = useState({
    length: false,
    uppercase: false,
    number: false,
    special: false
  });

  const closePopup = () => setPopup({ message: "", type: "info" });

  // ðŸ”¹ Fetch admin email from Realtime DB
  const fetchAdminEmail = async () => {
    const db = getDatabase();
    const otpEmailRef = ref(db, "admin_information/admin_email_otp");
    const snapshot = await get(otpEmailRef);
    if (snapshot.exists()) return snapshot.val();
    throw new Error("Admin OTP email not found in database.");
  };

  // ðŸ”¹ Send OTP using EmailJS
  const sendOtpToAdminEmail = async () => {
    try {
      const adminEmail = await fetchAdminEmail();
      const otp = Math.floor(100000 + Math.random() * 900000).toString();
      setGeneratedOtp(otp);

      await emailjs.send(
        "service_j61dqwv",
        "template_jijquaz",
        { email: adminEmail, otp },
        "evCxW5Fmv_kR4q8gh"
      );

      setOtpSent(true);
      setPopup({ message: "OTP sent to admin email.", type: "success" });
    } catch (err) {
      console.error(err);
      setPopup({ message: "Failed to send OTP: " + err.message, type: "error" });
    }
  };

  // ðŸ”¹ Handle password change and check criteria
  const handlePasswordChange = (e) => {
    const value = e.target.value;
    setPassword(value);

    setCriteria({
      length: value.length >= 6,
      uppercase: /[A-Z]/.test(value),
      number: /\d/.test(value),
      special: /[!@#$%^&*(),.?":{}|<>]_/.test(value)
    });
  };

  const isPasswordValid = Object.values(criteria).every(Boolean);

  // ðŸ”¹ Handle Signup
  const handleSignup = async (e) => {
    e.preventDefault();
    const email = e.target.email.value.trim();

    if (!isPasswordValid) {
      setPopup({ message: "Password does not meet all criteria.", type: "error" });
      return;
    }

       try {
      await setPersistence(auth, browserLocalPersistence);
      const userCredential = await signInWithEmailAndPassword(auth, email, password);

      if (!userCredential.user.emailVerified) {
        showPopup("Please verify your email before logging in.", "error");
        await auth.signOut();
        return;
      }

      localStorage.setItem("session", JSON.stringify({ email, timestamp: Date.now() }));
      showPopup("Login successful!", "success");
      setTimeout(() => navigate("/dashboard"), 1000);

    } catch (error) {
      console.error(error);
      showPopup("Invalid email or password.", "error");
      e.target.password.value = "";
    }
  };


  const checkIcon = (met) => (
    <span style={{ color: met ? "green" : "red", marginRight: "5px" }} 
          dangerouslySetInnerHTML={{ __html: `<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-check2-circle" viewBox="0 0 16 16">
            <path d="M2.5 8a5.5 5.5 0 0 1 8.25-4.764.5.5 0 0 0 .5-.866A6.5 6.5 0 1 0 14.5 8a.5.5 0 0 0-1 0 5.5 5.5 0 1 1-11 0"/>
            <path d="M15.354 3.354a.5.5 0 0 0-.708-.708L8 9.293 5.354 6.646a.5.5 0 1 0-.708.708l3 3a.5.5 0 0 0 .708 0z"/>
          </svg>` }} />
  );

  return (
    <div className="login-container">
      <LoginPopup message={popup.message} type={popup.type} onClose={closePopup} />

      <div className="card">
        <h3 className="text-center">Create Admin Account</h3>
        <p className="text-center">Sign up with OTP verification</p>

        <form onSubmit={handleSignup}>
          <div className="mb-3 text-start">
            <label htmlFor="email" className="form-label">Admin Email</label>
            <input type="email" id="email" className="form-control" placeholder="admin@example.com" required />
          </div>

          <div className="mb-3 text-start">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              id="password"
              className="form-control"
              placeholder="********"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            {password && (
              <ul style={{ fontSize: "0.9em", marginTop: "5px", listStyle: "none", paddingLeft: 0 }}>
                <li>{checkIcon(criteria.length)} At least 6 characters</li>
                <li>{checkIcon(criteria.uppercase)} At least 1 uppercase letter</li>
                <li>{checkIcon(criteria.number)} At least 1 number</li>
                <li>{checkIcon(criteria.special)} At least 1 special character</li>
              </ul>
            )}
          </div>

          {otpSent && (
            <div className="mb-3 text-start">
              <label htmlFor="otp" className="form-label">Enter OTP</label>
              <input
                type="text"
                id="otp"
                className="form-control"
                placeholder="6-digit OTP"
                value={enteredOtp}
                onChange={(e) => setEnteredOtp(e.target.value)}
                required
              />
            </div>
          )}

          <button type="submit" className="btn-primary" disabled={!isPasswordValid}>
            {otpSent ? "Complete Signup" : "Send OTP"}
          </button>
        </form>

        <div className="text-center mt-3">
          <p>
            Already have an account?{" "}
            <button
              onClick={() => navigate("/login")}
              className="btn-link"
              style={{ border: "none", background: "none", color: "#0d6efd", cursor: "pointer" }}
            >
              Login
            </button>
          </p>
        </div>
      </div>
    </div>
  );
}
